import { LiveAnnouncer } from '@angular/cdk/a11y';
import { Component, ViewChild, ViewEncapsulation } from '@angular/core';
import { MatSort, Sort } from '@angular/material/sort';
import { MatTableDataSource } from '@angular/material/table';

@Component({
  selector: 'app-hoteis',
  templateUrl: './hoteis.component.html',
  styleUrls: ['./hoteis.component.scss'],
  encapsulation: ViewEncapsulation.None
})
export class HoteisComponent {
  displayedColumns: string[] = ['position', 'name', 'weight', 'symbol', 'acoes'];
  dataSource = ELEMENT_DATA;
  clickedRows = new Set<PeriodicElement>();
}

export interface PeriodicElement {
  name: string;
  position: number;
  weight: number;
  symbol: string;
  acoes: string;
}
const ELEMENT_DATA: PeriodicElement[] = [
  { position: 1, name: 'Hydrogen', weight: 1.0079, symbol: 'H', acoes: 'teste' },
  { position: 2, name: 'Helium', weight: 4.0026, symbol: 'He', acoes: 'teste' },
  { position: 3, name: 'Lithium', weight: 6.941, symbol: 'Li', acoes: 'teste' }
];
